import React, { Component, ReactDOM } from 'react';
import logo from '../logo.svg';
// import '../css/Visual.css';
import '../css/Visual2.css';
import { ContextMenu, MenuItem, ContextMenuTrigger } from "react-contextmenu";
var d3 = require('d3');

export class Visual2 extends Component {

    componentWillMount(){

    }
    componentDidMount(){
        var width = 960,
            height = 500;


        var force = d3.layout.force()
            .charge(-120)
            .linkDistance(30)
            .size([width, height]);

        var svg = d3.select("body").append("svg")
            .attr("width", width)
            .attr("height", height);

        d3.json("miserables.json", function(error, graph) {
        force
            .nodes(graph.nodes)
            .links(graph.links)
            .start();

        graph.links.forEach(function (d) {
            d.group = Math.floor(Math.random() * 6)
        });

        var link = svg.selectAll(".link")
            .data(graph.links)
            .enter().append("line")
            .attr("class", "link")
            .style("stroke-width", function(d) { return Math.sqrt(d.value); })
            .style("stroke", function(d) { return color(d.group); });

        var node = svg.selectAll(".node")
            .data(graph.nodes)
            .enter().append("circle")
            .attr("class", "node")
            .attr("r", 5)
            .style("fill", function(d) { return color(d.group); })
            .call(force.drag);

        node.append("title")
            .text(function(d) { return d.name; });

        force.on("tick", function() {
            link.attr("x1", function(d) { return d.source.x; })
                .attr("y1", function(d) { return d.source.y; })
                .attr("x2", function(d) { return d.target.x; })
                .attr("y2", function(d) { return d.target.y; });

            node.attr("cx", function(d) { return d.x; })
                .attr("cy", function(d) { return d.y; });
        });
        });


            //const data = require("../JSON/stuff2.json");
            //console.log(data.data);

            // const data = {
            //     nodes: [{ id: 'Harry', color: "lightgreen", size: 1200}, { id: 'Sally', size: 400, color: "red" }, { id: 'Alice', size: 650, color: "blue" }],
            //     links: [{ source: 'Harry', target: 'Sally' }, { source: 'Harry', target: 'Alice' }]
            // };
        //   const data = "../JSON/stuff.json"
    }

    state = {
        modalDisplay: 'none',
        modalOpacity: 0,
        data: {
            nodes: [{ id: 'Harry', color: "lightgreen", size: 1200}, { id: 'Sally', size: 400, color: "red" }, { id: 'Alice', size: 650, color: "blue" }],
            links: [{ source: 'Harry', target: 'Sally' }, { source: 'Harry', target: 'Alice' }]
        }
    }

    constructor(props){
        // const url = 'https://raw.githubusercontent.com/DealPete/forceDirected/master/countries.json';
        super(props);
            
        
        
}




    render(state) {

        return (
            <div>
                    <ContextMenuTrigger id="some">
                    <div className='chartContainer'>
                        <svg className='chart'>
                        </svg>
                    </div>
                    </ContextMenuTrigger>

                    <ContextMenu id="some">
                        <MenuItem data={{foo: 'bar'}} onClick={this.handleClick}>
                        ContextMenu Item 1
                        </MenuItem>
                        <MenuItem data={{foo: 'bar'}} onClick={this.handleClick}>
                        ContextMenu Item 2
                        </MenuItem>
                        <MenuItem divider />
                        <MenuItem data={{foo: 'bar'}} onClick={this.handleClick}>
                        ContextMenu Item 3
                        </MenuItem>
                    </ContextMenu>
          </div>
        )
    }
}
