import React, { Component, ReactDOM } from 'react';
import logo from '../logo.svg';
//import logo from './logo2.png'
import '../css/Visual.css';
import { Graph } from 'react-d3-graph';
var fs = require('fs');
var d3 = require('d3');

export class Visual2 extends Component {
    
    componentDidMount(){
        // console.log(file);
    }

    constructor(props){
        super(props);
    }

    state = {
        modalTop: mouseY,
        modalLeft: mouseX,
        modalDisplay: 'none',
        modalOpacity: 0,
        data: {
            nodes: [{ id: 'Harry', color: "lightgreen", size: 1200}, { id: 'Sally', size: 400, color: "red" }, { id: 'Alice', size: 650, color: "blue" }],
            links: [{ source: 'Harry', target: 'Sally' }, { source: 'Harry', target: 'Alice' }]
        }
    }


    render(state) {

        return (
            <div onMouseMove={function(e){
                mouseX = e.clientX;
                mouseY = e.clientY;
                console.log(mouseX);
                console.log(mouseY);
            }}>
                <Graph
                    id="graph-id" // id is mandatory, if no id is defined rd3g will throw an error
                    data={this.state.data}
                    config={myConfig}
                    onClickNode={this.onClickNode}
                    onRightClickNode={this.onRightClickNode}
                    onClickGraph={this.onClickGraph}
                    onClickLink={this.onClickLink}
                    onRightClickLink={this.onRightClickLink}
                    onMouseOverNode={this.onMouseOverNode}
                    onMouseOutNode={this.onMouseOutNode}
                    onMouseOverLink={this.onMouseOverLink}
                    onMouseOutLink={this.onMouseOutLink}
                />;
                <div style={{    
                    position: "absolute",
                    top: this.state.modalTop,
                    right: 0,
                    left: this.state.modalLeft,
                    right: 0,
                    width: 200,
                    height: 200,
                    display: this.state.modalDisplay,
                    backgroundColor: "yellow",
                    opacity: this.state.modalOpacity,
                    }}/>
          </div>
        )
    }
}
