import React, { Component, ReactDOM } from 'react';
import logo from '../logo.svg';
//import logo from './logo2.png'
import '../css/Visual.css';
// import { Graph } from 'react-d3-graph';
import { ContextMenu, MenuItem, ContextMenuTrigger } from "react-contextmenu";
var d3 = require('d3');

export class Visual2 extends Component {
    
    componentDidMount(){
        // console.log(file);
    }

    state = {
        modalDisplay: 'none',
        modalOpacity: 0,
        data: {
            nodes: [{ id: 'Harry', color: "lightgreen", size: 1200}, { id: 'Sally', size: 400, color: "red" }, { id: 'Alice', size: 650, color: "blue" }],
            links: [{ source: 'Harry', target: 'Sally' }, { source: 'Harry', target: 'Alice' }]
        }
    }

    constructor(props){
        const url = 'https://raw.githubusercontent.com/DealPete/forceDirected/master/countries.json';
    

        fetch(url).then(res => {
            const data = res;

            // const data = {
            //     nodes: [{ id: 'Harry', color: "lightgreen", size: 1200}, { id: 'Sally', size: 400, color: "red" }, { id: 'Alice', size: 650, color: "blue" }],
            //     links: [{ source: 'Harry', target: 'Sally' }, { source: 'Harry', target: 'Alice' }]
            // };
        //   const data = "../JSON/stuff.json"
          
          const width = 640,
                height = 480;
          
          //Initializing chart
          const chart = d3.select('.chart')
            .attr('width', width)
            .attr('height', height);
          
          //Creating tooltip
          const tooltip = d3.select('.container')
            .append('div')
            .attr('class', 'tooltip')
            .html('Tooltip');
          
          //Initializing force simulation
          const simulation = d3.forceSimulation()
            .force('link', d3.forceLink())
            .force('charge', d3.forceManyBody())
            .force('collide', d3.forceCollide())
            .force('center', d3.forceCenter(width / 2, height / 2))
            .force("y", d3.forceY(0))
            .force("x", d3.forceX(0));
          
          
          //Drag functions
          const dragStart = d => {
            if (!d3.event.active) simulation.alphaTarget(0.3).restart();
            d.fx = d.x;
            d.fy = d.y;
          };
          
          const drag = d => {
            d.fx = d3.event.x;
            d.fy = d3.event.y;
          };
          
          const dragEnd = d => {
            if (!d3.event.active) simulation.alphaTarget(0);
            d.fx = null;
            d.fy = null;
          }
          
          //Creating links
          const link = chart.append('g')
            .attr('class', 'links')
            .selectAll('line')
            .data(data.links).enter()
            .append('line');
          
          //Creating nodes
          const node = d3.select('.chartContainer')
            .selectAll('div')
            .data(data.nodes).enter()
            .append('div')
            .attr('class', d => {return 'flag flag-' + d.code;})
            .call(d3.drag()
               .on('start', dragStart)
               .on('drag', drag)
               .on('end', dragEnd)
            ).on('mouseover',d => {
              tooltip.html(d.country)
                .style('left', d3.event.pageX + 5 +'px')
                .style('top', d3.event.pageY + 5 + 'px')
                .style('opacity', .9);
            }).on('mouseout', () => {
              tooltip.style('opacity', 0)
                .style('left', '0px')
                .style('top', '0px');
            });
          
          //Setting location when ticked
          const ticked = () => { 
              link
              .attr("x1", d => { return d.source.x; })
              .attr("y1", d => { return d.source.y; })
              .attr("x2", d => { return d.target.x; })
              .attr("y2", d => { return d.target.y; });
    
          node.attr("style", d => { 
                return 'left: ' + d.x + 'px; top: ' + (d.y + 72) + 'px'; 
              });
          };
          
          //Starting simulation
          simulation.nodes(data.nodes).on('tick', ticked);
          
          simulation.force('link').links(data.links);
        
        
    });
}




    render(state) {

        return (
            <div>
                    <ContextMenuTrigger id="some">
                    <div className='chartContainer'>
                        <svg className='chart'>
                        </svg>
                    </div>
                    </ContextMenuTrigger>

                    <ContextMenu id="some">
                        <MenuItem data={{foo: 'bar'}} onClick={this.handleClick}>
                        ContextMenu Item 1
                        </MenuItem>
                        <MenuItem data={{foo: 'bar'}} onClick={this.handleClick}>
                        ContextMenu Item 2
                        </MenuItem>
                        <MenuItem divider />
                        <MenuItem data={{foo: 'bar'}} onClick={this.handleClick}>
                        ContextMenu Item 3
                        </MenuItem>
                    </ContextMenu>
          </div>
        )
    }
}
