import React, { Component } from 'react';
import logo from '../logo.svg';
//import logo from './logo2.png'
import '../css/App.css';
import '../css/Visual.css';
var d3 = require("d3");
// var links = [
//     {
//         "id": '0',
//         "name": "Sufjan Stevens", 
//         "followers": 10137, 
//         "following": 0,
//         "tracks": 323,
//         "likes": 0,
//         "comments": 0,
//         "follows": [ '1', '2', '3' ]
//     },
//     {
//         "id": '1',
//         "name": "Future", 
//         "followers": 9798253, 
//         "following": 2,
//         "tracks": 525,
//         "likes": 0,
//         "comments": 0,
//         "follows": []
//     },
//     {
//         "id": '2',
//         "name": "Jdaflame", 
//         "followers": 261, 
//         "following": 88,
//         "tracks": 9,
//         "likes": 831,
//         "comments": 186,
//         "follows": [ '1', '3' ]
//     },
//     {
//         "id": '3',
//         "name": "Vibekatcher", 
//         "followers": 1170, 
//         "following": 1571,
//         "tracks": 28,
//         "likes": 2862,
//         "comments": 1151,
//         "follows": ['0', '1', '2' ]
//     },
// ]

var links = [
    { source: "Baratheon", target: "Lannister" },
    { source: "Baratheon", target: "Stark"},
    { source: "Lannister", target: "Stark"},
];

var nodes = {};


export class Visual extends Component {
    
    constructor(props){

        super(props);

        var width = 640;
        var height = 480;

        links.forEach(function(link) {
            link.source = nodes[link.source] ||
                (nodes[link.source] = {name: link.source});
            link.target = nodes[link.target];
                (nodes[link.target] = {name: link.target});
        })

        var svg = d3.select('body').append('svg')
            .attr('width', width)
            .attr('height', height);

        var force = d3.layout.force()
            .size([width, height])
            .nodes(d3.values(nodes))
            .links(links)
            .on("tick", tick)
            .linkDistance(300)
            .start();

        var link = svg.selectAll('link')
            .data(links)
            .enter().append('line')
            .attr('class', 'link');

        var node = svg.selectAll('.node')
            .data(force.nodes())
            .enter().append('circle')
            .attr('class', 'node')
            .attr('r', width * 0.03)

        function tick(e) {
            node.attr('cx', function(d) {return d.x})
                .attr('cy', function(d) {return d.y})
                .call(force.drag);

            link.attr('x1', function(d) {return d.source.x})
                .attr('y1', function(d) {return d.source.y})
                .attr('x2', function(d) {return d.target.x})
                .attr('y2', function(d) {return d.target.y})
        }


    }



    

    render() {

        return (
        <div className="App">
            <header className="App-header">
                <div>

                </div>
            </header>
        </div>
        );
    }
}
