        focusAnimationDuration: 0.25,
        focusZoom: 1,
        highlightOpacity: 1,
        linkHighlightBehavior: false,
        maxZoom: 8,
        minZoom: 0.1,
        nodeHighlightBehavior: false,
        panAndZoom: false,
        staticGraph: false,
    d3: {
        alphaTarget: 0.05,
        gravity: -500,
