            var x = Math.floor(Math.random() * 4) + 1
            var y = Math.floor(Math.random() * 4) + 1
            var obj = { id: 'Harry', color: "lightgreen", size: 1200}
            if(x === 1){ link=   obj={ id: "Vibekatcher", size: 800 }} else if(x === 2) {obj={ id: "Jdaflame", size: 800 }} else if(x === 3) {obj={ id: "Yoozer", size: 400 }} else if(x === 4){obj={ id: "Future", size: 500 }}
            if(y === 1){ obj.color = "blue" } else if(y === 2) {obj.color = "green"} else if(y === 3) {obj.color = "yellow"} else if(y === 4){obj.color = "red"}
            
            //console.log(obj);
